import React, { useState, useEffect } from 'react';
import { Card, Button } from 'react-bootstrap/';

function MyAppointments( { me } ){
    const [appointments,setappointments] = useState([])
    useEffect(()=>{
        if (me.id>0){
        fetch(`/patients/${me.id}`)
        .then(resp=>resp.json())
        .then(data=>setappointments(data.appointments))
    }}
    ,[me])
    return (
        <header>
            <h1>Appointments:</h1>
            {appointments.map(appointment=>{
               return(
                <Card key={appointment.id} style={{ width: '18rem' }}>
                <Card.Body>
                    <Card.Title>{appointment.name}</Card.Title>
                    <Card.Subtitle className="mb-2 text-muted">{appointment.dose}</Card.Subtitle>
                    <Card.Text>
                        {appointment.schedule}
                    </Card.Text>
                    <Card.Text>
                        {appointment.provider}
                    </Card.Text>
                    <Button variant="primary">I don't take this anymore.</Button>
                </Card.Body>
                </Card>) 

            })}
        </header>
    )
}

export default MyAppointments;
