import React, { useState, useEffect } from 'react';
import MyPrescriptions from './MyPrescriptions';

function Home( { me }){
    const [scripts,setScripts] = useState([])
    useEffect(()=>{
        if (me.id>0){
        fetch(`/patients/${me.id}`)
        .then(resp=>resp.json())
        .then(data=>setScripts(data.prescriptions))
    }}
    ,[me])
    return (
        <header>
            Home
            <p>pt.id: {me.id?me.id:"not logged in"} </p>
            {scripts.map(script=>{
               return(
                <div key={script.id}>
                    <h1>Prescriptions:</h1>
                    <p>name: {script.name}</p>
                    <p>dose: {script.dose}</p>
                    <p>schedule: {script.schedule}</p>
                </div>
               ) 

            })}
        </header>
    )
}

export default Home;
