import { Form, Button} from 'react-bootstrap/';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import React,{ useState, useEffect } from 'react';
import { Route, Routes,useNavigate,useParams } from "react-router"


function Login(){
    let navigate= useNavigate();
    const [form,setForm]=useState({
        "email": "test",
        "password": "1234"
      })
    function handleFormChange(e){
        setForm({...form,[e.target.name]:e.target.value})
    }
    function handleSubmit(e){
        e.preventDefault()
        fetch("/login",{
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(form)
      }).then(resp => resp.json())
        navigate("/me")
          setForm({
            "email": "",
            "password": ""
          })} 
    return (
        <form onSubmit={handleSubmit} style={{ width: '40rem',margin: 'auto' }}>
          <h2>Log In to Your Account</h2>

          <Row className='mt-2'>
            <Form.Label column lg={2}>
              Email
            </Form.Label>
            <Col xs={7}>
              <Form.Control type="username" placeholder="your-name@your-email.com" name='name' value={form.email} onChange={handleFormChange}/>
            </Col>
          </Row>

          <Row className='mt-2'>
            <Form.Label column lg={2}>
              Password
            </Form.Label>
            <Col xs={7}>
                {/* change type to password to hide password field */}
              <Form.Control type="password" placeholder="Password here" name='password' value={form.password} onChange={(e)=>handleFormChange(e)} />
            </Col>
          </Row>

          <Button onSubmit={handleSubmit} variant="primary" type="submit">
            Login
          </Button>
        </form>
      )
}

export default Login
